/**
 * Get the SVG icon for a condition by name
 * @param iconName The name of the condition icon (e.g., "blinded", "charmed")
 * @returns The SVG string, or undefined if not found
 */
export function getConditionIconSvg(iconName: string): string | undefined {
  return CONDITION_ICONS[iconName.toLowerCase()];
}

/**
 * Get all available condition icon names
 */
export function getAvailableConditionIcons(): string[] {
  return Object.keys(CONDITION_ICONS);
}
